import javax.swing.*;import java.awt.*;import java.awt.event.MouseAdapter;import java.awt.event.MouseEvent;import java.awt.event.MouseListener;public class DrawingJPanel extends JPanel implements MouseListener {    private final int SCREEN_WIDTH = 1000;    private final int SCREEN_HEIGHT = 1000;    private Point point = new Point(10,10);    private Point point1 = new Point(200,200);    private Sharp line = new Line(point,point1);    DrawingJPanel(){        this.setPreferredSize(new Dimension(SCREEN_WIDTH,SCREEN_HEIGHT));        this.setBackground(Color.black);        this.setFocusable(true);        this.addMouseListener(this);    }    @Override    protected void paintComponent(Graphics g) {        super.paintComponent(g);        line.draw(g);    }    @Override    public void mouseClicked(MouseEvent e) {        repaint();    }    @Override    public void mousePressed(MouseEvent e) {    }    @Override    public void mouseReleased(MouseEvent e) {    }    @Override    public void mouseEntered(MouseEvent e) {    }    @Override    public void mouseExited(MouseEvent e) {    }}